module.exports = function (_, DayjsClass, dayjsFactory) {
  dayjsFactory.max = function (...values) {
    return values.reduce((current, value) => {
      const candidate = dayjsFactory(value);
      if (!candidate.isValid()) return current;
      if (!current || candidate.valueOf() > current.valueOf()) {
        return candidate;
      }
      return current;
    }, null) || dayjsFactory(NaN);
  };

  dayjsFactory.min = function (...values) {
    return values.reduce((current, value) => {
      const candidate = dayjsFactory(value);
      if (!candidate.isValid()) return current;
      if (!current || candidate.valueOf() < current.valueOf()) {
        return candidate;
      }
      return current;
    }, null) || dayjsFactory(NaN);
  };
};
