function createMiddleware() {
  return function mockMulterMiddleware(req, res, next) {
    if (typeof next === 'function') next();
  };
}

function multer() {
  const middleware = createMiddleware();
  middleware.single = function () {
    return createMiddleware();
  };
  middleware.array = function () {
    return createMiddleware();
  };
  middleware.fields = function () {
    return createMiddleware();
  };
  middleware.none = function () {
    return createMiddleware();
  };
  middleware.any = function () {
    return createMiddleware();
  };
  return middleware;
}

multer.diskStorage = function (config = {}) {
  return { ...config };
};

module.exports = multer;
